import React from 'react';
import PropTypes from 'prop-types';

var Accordion = function Accordion(props) {

  var childrenWithProps = props.children.map(function (child, index) {
    return React.cloneElement(child, {
      id: props.accordionId,
      arrow: props.arrow,
      key: props.accordionId + '_panel_' + index
    });
  });

  return React.createElement(
    'div',
    {
      className: 'accordion',
      role: 'tablist'
    },
    childrenWithProps
  );
};

Accordion.defaultProps = {
  arrow: 'left'
};

Accordion.propTypes = process.env.NODE_ENV !== "production" ? {
  accordionId: PropTypes.string.isRequired,
  arrow: PropTypes.oneOf(['left', 'right', 'none'])
} : {};

export default Accordion;